<?php
 namespace Alipay\Request; class AlipaySystemOauthTokenRequest extends AbstractAlipayRequest { private $code; private $grantType; private $refreshToken; public function setCode($code) { $this->code = $code; $this->apiParams['code'] = $code; } public function getCode() { return $this->code; } public function setGrantType($grantType) { $this->grantType = $grantType; $this->apiParams['grant_type'] = $grantType; } public function getGrantType() { return $this->grantType; } public function setRefreshToken($refreshToken) { $this->refreshToken = $refreshToken; $this->apiParams['refresh_token'] = $refreshToken; } public function getRefreshToken() { return $this->refreshToken; } } 