<?php
 use Alipay\AlipayRequestFactory; $aop = require __DIR__ . '/_bootstrap.php'; $apiName = basename(__FILE__, '.php'); $request = AlipayRequestFactory::create($apiName, [ 'grant_type' => 'authorization_code', 'code' => $_GET['authcode'] ]); try { $data = $aop->execute($request)->getData(); print_r($data); } catch (\Exception $ex) { http_response_code(500); print_r($ex); }